$(function () {
  $('[data-toggle="tooltip"]').tooltip({
    template: '<div class="tooltip tooltip-custom"><div class="tooltip-arrow"></div><div class="tooltip-inner"></div></div>'
  });
})

$(document).ready(function(){
  $("#account-number").mask("999 999 999 999",{placeholder:" "});
});

function loading() {
  $('#loading').toggle();
}

function toggleWarning() {
  $('#modalWarning').modal('toggle');
}

const sessionDuration = 15 * 60; // 15 minutes
var sessionInterval = null
function countdown(){
  var t = sessionDuration;
  setInterval(() => {
    console.log(t);
  }, 1000);
}

function stopCountdown(){
  clearInterval(sessionInterval);
}